pragma solidity ^0.5.0;

import "../contracts/Ilighthouse.sol";

//Example for having customers pay to view videos at 10 cents a minute
contract ExampleCode {

    ILighthouse  public myLighthouse;                       //ILighthouse is a struct we import. Create an instance of it
    mapping(address => uint) public balances;               //Records uses's money
    mapping(address => uint) public expiry;                 //Records how long a user has paid to watch videos, specifically the time when
                                                            //a user is unable to continue watching videos (time expires)
    uint public spentFees;
    address public constant alice =  0x31EFd75bc0b5fbafc6015Bd50590f4fDab6a3F22;

    constructor(ILighthouse _myLighthouse) public {         //When deploying the migration, must pass constructor arguments
                                                            //I need to pass an instance of ILighthouse to get data from when I deploy this
        myLighthouse = _myLighthouse;
    }

    /*
      This is my fallback function, only 1 allowed per contract, it cannot have a name or return value
      This function is called if this smart contract is called and no fuctions match, or it is supplied
      plain ether. In order for this contract to receive the ether, this function must be marked payable.

      People will send ether to this contract and this function will be called, putting their ether into
      their balances, then when the user wants to purchase time, buyTime will deduct from their balances.
    */

    // function() public payable {
    //     balances[msg.sender] += msg.value;
    // }

    function buyTime(uint minutesToBuy) public {
        require(minutesToBuy != 0, "nothing to buy");
        uint tenCentsOfETH;
        bool ok;
        (tenCentsOfETH,ok) = myLighthouse.peekData();
        uint fee = tenCentsOfETH * minutesToBuy;
        require(fee / minutesToBuy == tenCentsOfETH,"Overflow");
        require(fee < balances[msg.sender],"Not enough funds");
        expiry[msg.sender] = now + minutesToBuy * 1 minutes;            //Where is the fee's deducted from the viewer's account?
        spentFees += fee;
    }

// Checks if a customer (the smart contract that calls this) is within the paid time period
    function inPaidTime() public view returns (bool) {
        return now < expiry[msg.sender];
    }

// Allows Alice to get her payments
    function withdrawFees() public {
        require(alice == msg.sender, "Unauthorised");
  //      alice.transfer(spentFees);
        spentFees = 0;
    }
}


