pragma solidity ^0.5.0;

import "../contracts/Ilighthouse.sol";

contract GoldShop {

    ILighthouse  public myLighthouse;                       //ILighthouse is a struct we import. Create an instance of it

    //Since solidity 0.5.0 the address must be marked payable to send it ether via address.transfer()

    constructor(ILighthouse _myLighthouse) public {
        myLighthouse = _myLighthouse;
    }

    function printPrice() public returns (uint) {

      uint troyOunceOfGold = 40;     //One troy ounce of gold costs 40 USD
      bool ok;
  //    uint zero = 1;
      (troyOunceOfGold,ok) = myLighthouse.peekData();    //Revert Error occurs here (Because there is no lighthouse deployed locally -- doesnt return)
  //    log0(bytes32(troyOunceoOfGold));
      return troyOunceOfGold;
    //    return zero;
    }

    function worthSelling(uint troyOunces, uint dollars) public returns (bool){   //Is a piece of jewlery worth selling second hand? If not, better to melt and sell raw gold
      uint oneOz = printPrice();
      uint goldVal = oneOz * troyOunces;

      bool isWorth;

      if(goldVal < dollars){            //raw gold worth less than second hand purchase offer, worth it to sell
        isWorth = true;
      }
      else{
        isWorth = false;
      }
      return isWorth;
    }


}

//Create a gold shop which sells gold coins using the price of gold from a lighthouse. Updates the display cost with new prices

/* ------------------------------Revised Scenario-----------------------------------
People who want to get rid of old gold jewlery can either sell it second hand or melt it and sell the gold. But it can be hard to know which is
more profitable. That's where my service comes in. Customers call my smart contract with two parameters, troyOunces and dollars, describing
how much gold is in a piece of jewlery and the best offer customers have so far for selling it second hand. My contract will get the current
exchange rate of gold, and calculate whether or not it is worth it to sell the jewlery second hand.

*/
